import React from 'react'
import { useState, useEffect } from 'react'
import Map, { Marker } from 'react-map-gl'
import api from '../../api/contactAPI'



const MapB = () => {

  const [items, setItems] = useState([]);

  useEffect (() => {
    const fetchContacts = async () => {
      try {
        const response = await api.get('./contact');
        setItems(response.data);
      } catch (err) {
        //console.log(err.response.message);
        console.log("error failed request")
      }
    }
    fetchContacts();
  }, [items]);








  return (
    /*
    <>
    <div className='border-green-600 border-solid border-2 
    h-full w-3/4 float-left flex' style={{ maxHeight: 'calc(100vh - 65px)'}} >
      <div id='map' ref={mapContainer} className='w-[95%] h-[95%] m-auto'

      />
    </div>
    </>
    */

    <div className='border-green-600 border-solid border-2 
    h-full w-3/4 float-left flex' style={{ maxHeight: 'calc(100vh - 65px)'}} >
      <div className='h-[95%] w-[95%] m-auto'>
        <Map
          mapboxAccessToken='pk.eyJ1IjoieXZlc3RhbiIsImEiOiJjbGRwczllamkxNjg1M3F0NmpmeW1zMHhhIn0.LpkG5sF33tcYKwZ4cbuEwg'
          initialViewState={{
            longitude: '6.019018007537025',
            latitude: '47.240500749279',
            zoom: '14'
          }}


          mapStyle='mapbox://styles/mapbox/streets-v11'
          attributionControl=''
          maxBounds={[[6.019018007537025 - 0.1, 47.240500749279 - 0.1], [6.019018007537025 + 0.1, 47.240500749279 + 0.1]]}
        >
          {
            items.map((val, index) => {
              return (
                  val.position ? (
                    <Marker 
                      key={index}
                      longitude={val.position.longitude}
                      latitude={val.position.latitude}
                    />
                  ) : ""

              )

            })
          }



        </Map>
      </div>
    </div>

  )
}

export default MapB
