import React from 'react'

const Map = () => {
  let src="https://api.mapbox.com/mapbox-gl-js/v2.12.0/mapbox-gl.js"
  mapboxgl.accessToken = 'pk.eyJ1IjoieXZlc3RhbiIsImEiOiJjbGRwczllamkxNjg1M3F0NmpmeW1zMHhhIn0.LpkG5sF33tcYKwZ4cbuEwg';
  const mapboxClient = mapboxSdk({ accessToken: mapboxgl.accessToken });
  mapboxClient.geocoding
  .forwardGeocode({
  query: 'Wellington, New Zealand',
  autocomplete: false,
  limit: 1
  })
  .send()
  .then((response) => {
  if (
  !response ||
  !response.body ||
  !response.body.features ||
  !response.body.features.length
  ) {
  console.error('Invalid response:');
  console.error(response);
  return;
  }
  const feature = response.body.features[0];

  const map = new mapboxgl.Map({
  container: 'map',
  // Choose from Mapbox's core styles, or make your own style with Mapbox Studio
  style: 'mapbox://styles/mapbox/streets-v12',
  center: feature.center,
  zoom: 10
  });

  // Create a marker and add it to the map.
  new mapboxgl.Marker().setLngLat(feature.center).addTo(map);
  });



  return (
    <>
    <link href="https://api.mapbox.com/mapbox-gl-js/v2.12.0/mapbox-gl.css" rel="stylesheet"></link>
    <div className='border-green-600 border-solid border-2 
    h-full w-3/4 float-left' style={{ maxHeight: 'calc(100vh - 65px)'}} >
      Map
    </div>
    </>
  )
}

export default Map
