import React from 'react'
import { useState, useRef, useEffect } from 'react'
import mapboxgl, { Marker } from 'mapbox-gl';
//import ReactMapGL, { Marker } from 'mapbox-gl';
import api from '../../api/contactAPI'

const Map = () => {



  mapboxgl.accessToken = 'pk.eyJ1IjoieXZlc3RhbiIsImEiOiJjbGRwczllamkxNjg1M3F0NmpmeW1zMHhhIn0.LpkG5sF33tcYKwZ4cbuEwg';

  const mapContainer = useRef(null);
  const map = useRef(null);
  const [lng, setLng] = useState(-70.9);
  const [lat, setLat] = useState(42.35);
  const [zoom, setZoom] = useState(9);

  const [items, setItems] = useState([]);

  useEffect(() => {

    async function getPositions() {

      return new Promise((resolve, reject) => {
        // Récupération des positions depuis la base de données
        api.get("./contact")
          .then(positions => {
            resolve(positions);
          })
          .catch(error => {
            reject(error);
          });
      });
    }



    if (map.current) return; // initialize map only once
      map.current = new mapboxgl.Map({
      container: mapContainer.current,
      style: 'mapbox://styles/mapbox/streets-v12',
      center: [lng, lat],
      zoom: zoom,
      attributionControl: false
    });

    const marker = new mapboxgl.Marker()
            .setLngLat([-70, 42])
            .addTo(map.current);


    const initMarker = async () => {
      try {
        const pos = await getPositions();
        pos.data.forEach(e => {
          console.log("e =",e.position)
          if(e.position != undefined) {

            const marker = new mapboxgl.Marker()
            .setLngLat([Number(e.position.long), Number(e.position.lat)])
            .addTo(map.current);
          }else{
            console.log("not added")
          }
        });
      }catch(err) {
        console.log(err)
      }
    }
    initMarker();

  });





  return (

    <>
    <div className='border-green-600 border-solid border-2 
    h-full w-3/4 float-left flex' style={{ maxHeight: 'calc(100vh - 65px)'}} >
      <div ref={mapContainer} className='w-[95%] h-[95%] m-auto'

      />
    </div>
    </>

  )
}

export default Map
