import React from 'react'
import { ImCross } from 'react-icons/im'


const [formData, setFormData] = useState({
  textInput: "",
  textAreaInput: "",
  selectInput: "Option 1",
});

const handleChange = (event) => {
  setFormData({
    ...formData,
    [event.target.name]: event.target.value,
  });
};


const AddContact = (props) => {
    return (
        <div className='bg-gray-300 w-[400px] h-[500px] absolute top-[20%] right-[40%] rounded-[10px]'>
            <ImCross onClick={()=>{props.HandleToggleAdd()}} className='w-[30px] h-[30px] float-right mt-[10px] mr-[10px] p-[8px] hover:bg-slate-200 hover:rounded-full'></ImCross>
            <h1 className='font-medium text-[20px] text-center mt-[50px]' >Ajouter un contact</h1>
            <form>
      <div>
        <label htmlFor="textInput">Text Input:</label>
        <input
          type="text"
          id="textInput"
          name="textInput"
          value={formData.textInput}
          onChange={handleChange}
        />
      </div>
      <div>
        <label htmlFor="textAreaInput">Text Area Input:</label>
        <textarea
          id="textAreaInput"
          name="textAreaInput"
          value={formData.textAreaInput}
          onChange={handleChange}
        />
      </div>
      <div>
        <label htmlFor="selectInput">Select Input:</label>
        <select
          id="selectInput"
          name="selectInput"
          value={formData.selectInput}
          onChange={handleChange}
        >
          <option value="Option 1">Option 1</option>
          <option value="Option 2">Option 2</option>
          <option value="Option 3">Option 3</option>
        </select>
      </div>
    </form>
        </div>
  )
}

export default AddContact
